import React, { useEffect, useMemo, useRef, useState } from 'react'
import "../styles/Skills.css"
import Posts from './Posts'

const Skills = () => {

  const [posts, setPosts] = useState([]);
  const [totalCount, setTotalCount] = useState(0)
  const [limit, setLimit] = useState(15);
  const [page, setPage] = useState(1);
  const [arrayPages, setArrayPages] = useState([])
  const lastElement = useRef()
  const observer = useRef()


  function countPages (){
    for (let i = 0; i < totalCount; i++){
      setArrayPages(arrayPages => [...arrayPages, i+1])
    }
  }

  useEffect( () => {
    
    var callback = (entries, observer) => {
      console.log(entries)
      if (posts.length === 0) return
      if (observer.current) observer.current.disconect();
      if (entries[0].isIntersecting && page < totalCount){
        setPage(page +1)
      }
    }
    
    observer.current = new IntersectionObserver(callback);
    observer.current.observe(lastElement.current)
  }, [page] )


  useEffect( () => {
    fetchPosts();
  }, [page])

  useMemo ( () => {
    countPages()
  }, [totalCount]

  )

  async function fetchPosts() {
    await fetch(`https://jsonplaceholder.typicode.com/comments?_limit=${limit}&_page=${page}`)
      .then(response => (setTotalCount(Math.ceil(response.headers.get("x-total-count") / limit)), response.json()))
      .then(response => {posts.length === 0 ? (setPosts(response)) : (setPosts(posts => [...posts, ...response]))})
      .catch(err => console.error(err))
    }
  

  return (
    <div className='global_page'>
      <p className='title_name'>Here I implement some functions to show what I know and can do on React</p>
      <div className='block_of_posts'>
        {posts.map((posts, index)  => <Posts key={index} posts={posts} number={index +1} /> )}
      </div>
      <div ref={lastElement} className='horizon'></div>
      <div className='buttons_pages'>
        {arrayPages.map((index) => <div 
        onClick={() => setPage(index)}
        key={index}
        className={page === index ? "page page_current" : "page"}>{index}</div>)}
      </div>
    </div>
  )
}

export default Skills
