import React, { useEffect, useMemo, useState } from 'react'
import "../styles/Skills.css"
import Posts from './Posts'

const Skills = () => {

  const [posts, setPosts] = useState([]);
  const [totalCount, setTotalCount] = useState(0)
  const [limit, setLimit] = useState(15);
  const [page, setPage] = useState(1);
  const [arrayPages, setArrayPages] = useState([])
 
  function test (){
    for (let i = 0; i < totalCount; i++){
      setArrayPages(arrayPages => [...arrayPages, i+1])
    }
  }

  console.log(arrayPages)

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id)) 
  }

  useEffect( () => {
    fetchPosts();
  }, [])

  useEffect ( () => {
    test()
  }, [fetchPosts]
  )

  async function fetchPosts() {
    await fetch(`https://jsonplaceholder.typicode.com/comments?_limit=${limit}&_page=${page}`)
      .then(response => (setTotalCount(Math.ceil(response.headers.get("x-total-count") / limit)), response.json()))
      .then(response => (setPosts(response)))      
      .catch(err => console.error(err))
    
    }
  

  return (
    <div className='global_page'>
      <p className='title_name'>Here I implement some functions to show what I know and can do on React</p>
      <div className='block_of_posts'>
        {posts.map((posts, index)  => <Posts key={posts.id} remove={removePost} posts={posts} number={index +1} /> )}
        </div>
    </div>
  )
}

export default Skills
