import "../styles/Button.css"
import { useState } from 'react';
import Modal from './Modal'

const Button = ({style_Button, id}) => {
  
  const [modal, setModal] = useState(false);

  var isSchool = 0;
  var university = 0; 
  var monitoring = 0;
  var migration = 0;

  if (modal === 1){
    school = true;
  }
  else if (modal === 2){
    university = true;
  }
  else if (modal === 3){
    monitoring = true;
  }
  else if (modal === 4){
    migration = true;
  }

  return (
    <div>
      <button className={style_Button} onClick={() => setModal(id)}>More</button>
    is
    </div>
      )
}

export default Button
