import React, { useEffect, useState } from 'react'
import "../styles/Skills.css"

const Skills = () => {

  const [dev, setDev] = useState([])
  const [dop, setDop] = useState('')
  

  useEffect( () => {
    fetch(`https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${dop}`)
    .then(response => response.json())
    .then(response => setDev(response))
    .catch(err => console.error(err));
  }, [])

  return (
    <div className='global_page'>
      <p className='title_name'>Here I implement some functions to show what I know and can do on React</p>
      <div className='block_of_posts'>
      <input placeholder='enter' onChange={(e) => setDop(e.target.value)}/>
        <p>{dop}</p>
        {dev?.drinks?.map(({strDrink}, index) => {
                return (
                    <div className='op' key={index}>
                      <p>{strDrink}</p>
                      
                  </div>
                    
                )
            })}
      </div>
    </div>
  )
}

export default Skills
