import React, { useEffect, useState } from 'react'
import "../styles/Skills.css"
import Posts from './Posts'

const Skills = () => {

  const [posts, setPosts] = useState([])
  
  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  useEffect( () => {
    fetchPosts()
  }, [])

   function fetchPosts() {
    fetch('https://jsonplaceholder.typicode.com/comments')
      .then(response => response.json())
      .then(response => setPosts(response))
      .catch(err => console.error(err));
  }

  

  return (
    <div className='global_page'>
      <p className='title_name'>Here I implement some functions to show what I know and can do on React</p>
      <div className='block_of_posts'>
        {posts.map((posts, index) => <Posts remove={removePost} posts={posts} /> )}
        
        </div>
    </div>
  )
}

export default Skills
