import React from 'react'
import "../styles/Button.css"
import { useState } from 'react';
import Modal from './Modal'

const Button = ({style_Button, id}) => {
  
  const [modal, setModal] = useState(false);
  const [choose, setChoose] = useState();

  function Modal_window (){
    setModal(true);
    setChoose(id)
  }

  var isSchool = 0;
  var isUniversity = 0; 
  var isMonitoring = 0;
  var isMigration = 0;

  if (choose === 1){
    isSchool = true;
  }
  else if (choose === 2){
    isUniversity = true;
  }
  else if (choose === 3){
    isMonitoring = true;
  }
  else if (choose === 4){
    isMigration = true;
  }

  return (
    <div>
      <button className={style_Button} onClick={Modal_window}>More</button>
    <Modal visible={true} setVisible={setModal}></Modal>
    {isSchool ? <Modal visible={modal} setVisible={setModal}></Modal> : ""}
    </div>
      )
}

export default Button
